import React from 'react';
import './About.css';

function About() {
    return (
        <section className="about">
            <h2>À propos</h2>
            <p>
                Ce site vise à sensibiliser le public aux menaces pesant sur les océans à cause des activités humaines,
                en mettant en lumière des problèmes spécifiques et en proposant des solutions pour les combattre.
            </p>
        </section>
    );
}

export default About;
